import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Product() {
  const [products, setProducts] = useState([]);

  // Function to fetch products from backend
  const fetchProducts = async () => {
    try {
      // Send GET request to backend to fetch products
      const response = await axios.get('/products'); // Update the endpoint according to your API
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  useEffect(() => {
    // Fetch products when component mounts
    fetchProducts();
  }, []);

  return (
    <div>
      <div className="container-fluid bg-breadcrumb">
        {/* Breadcrumb code */}
      </div>
      <div className="container-fluid service py-5">
        <div className="container py-5">
          {/* Service section code */}
          <div className="row g-4 justify-content-center">
            {/* Iterate over products and display */}
            {products.map(product => (
              <div className="col-md-6 col-lg-4 col-xl-3 wow fadeInUp" key={product._id}>
                <div className="service-item rounded">
                  <div className="service-img rounded-top">
                    <img src={product.imageUrl} className="img-fluid rounded-top w-100" alt={product.title} />
                  </div>
                  <div className="service-content rounded-bottom bg-light p-4">
                    <div className="service-content-inner">
                      <h5 className="mb-4">{product.title}</h5>
                      <p className="mb-4">{product.description}</p>
                      {/* Additional actions */}
                      <a href="#" className="btn btn-primary rounded-pill text-white py-2 px-4 mb-2">Read More</a>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
