import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { client } from '../../clientaxios/Clientaxios';

export default function Admission() {
  const [formDataList, setFormDataList] = useState([]);
  const [loading, setLoading] = useState(true); // Added loading state

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await client.get('/study');
        console.log('Response from backend:', response.data);

        // Check if the data structure matches the expected structure
        const admissions = response.data.admissions || [];
        setFormDataList(admissions);
        setLoading(false); // Set loading to false once data is fetched
      } catch (error) {
        console.error('Error fetching form data:', error);

        if (error.response && error.response.data && error.response.data.error) {
          console.error('Server Error:', error.response.data.error);
        } else {
          console.error('Error fetching data. Please try again.');
        }
        setLoading(false); // Set loading to false in case of error
      }
    };

    fetchData();
  }, []);

  const handleDelete = async (id) => {
    try {
      await client.delete(`/study/${id}`);
      setFormDataList((prevData) => prevData.filter(formData => formData._id !== id));
    } catch (error) {
      console.error('Error deleting form data:', error);
      if (error.response && error.response.data && error.response.data.error) {
        console.error('Server Error:', error.response.data.error);
      } else {
        console.error('Error deleting data. Please try again.');
      }
    }
  };  

  return (
    <div>
      <div className="container-fluid">
        <div className="container mt-4">
          <h2>Admission Form</h2>
          {loading ? (
            <p>Loading...</p> // Display loading message while fetching data
          ) : formDataList.length > 0 ? (
            <table className="table">
              <thead>
                <tr>
                  <th>Full Name</th>
                  <th>Father's Name</th>
                  <th>Father's Occupation</th>
                  <th>Email</th>
                  <th>Date of Birth</th>
                  <th>Gender</th>
                  <th>Marital Status</th>
                  <th>Nationality</th>
                  <th>Religion</th>
                  <th>Mother Tongue</th>
                  <th>Educational Qualification</th>
                  <th>Phone Number</th>
                  <th>Address</th>
                  <th>Courses</th>
                  <th>Delete</th>
                </tr>
              </thead>
        
            </table>
          ) : (
            <p>No data available</p>
          )}
        </div>
      </div>
    </div>
  );
}
