import React, { useState, useEffect } from 'react';
import { client } from '../../clientaxios/Clientaxios';

export default function Treatments() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [briefDescription, setBriefDescription] = useState('');
  const [file, setFile] = useState(null);
  const [treatments, setTreatments] = useState([]);
  const [editingTreatment, setEditingTreatment] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFormSubmit = async () => {
    try {
      const requestData = new FormData();
      requestData.append('title', title);
      requestData.append('description', description);
      requestData.append('briefDescription', briefDescription);
      if (file) {
        requestData.append('image', file);
      }

      if (editingTreatment) {
        // If editing, send a PUT request to update the treatment
        await client.put(`/treatments/${editingTreatment._id}`, requestData);
        setEditingTreatment(null); // Reset editing state
      } else {
        // If not editing, send a POST request to create a new treatment
        await client.post('/treatments/resizx', requestData);
      }

      // Clear the form and fetch updated treatments
      clearForm();
      fetchTreatments();
    } catch (error) {
      console.error('Error submitting form:', error);
      if (error.response) {
        console.error('PUT request error:', error.response.data);
      }
    }
  };

  const handleEdit = (treatment) => {
    setTitle(treatment.title);
    setDescription(treatment.description);
    setBriefDescription(treatment.briefDescription);
    setEditingTreatment(treatment);
  };

  const handleDelete = async (id) => {
    try {
      await client.delete(`/treatments/${id}`);
      fetchTreatments();
    } catch (error) {
      console.error('Error deleting treatment:', error);
    }
  };

  const fetchTreatments = async () => {
    try {
      const response = await client.get('/treatments');
      setTreatments(response.data);
    } catch (error) {
      console.error('Error fetching treatments:', error);
    }
  };

  const clearForm = () => {
    setTitle('');
    setDescription('');
    setBriefDescription('');
    setFile(null);
    setEditingTreatment(null);
  };

  useEffect(() => {
    fetchTreatments();
  }, []);

  return (
    <div className="container-fluid">
      <div className="container">
        <div className="row">
          {/* Form section */}
          <div className="col-md-12 mt-4">
            <h2>Admin Treatments</h2>
            <form>
              {/* Form inputs */}
              <div className="row g-3">
                <div className="col-md-6">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                  />
                </div>
                <div className="col-md-6">
                  <textarea
                    className="form-control"
                    rows="3"
                    placeholder="Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                  />
                </div>
                <div className="col-md-6">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Brief Description"
                    value={briefDescription}
                    onChange={(e) => setBriefDescription(e.target.value)}
                  />
                </div>
                <div className="col-md-6">
                  <input
                    type="file"
                    accept="image/*"
                    className="form-control-file"
                    onChange={handleFileChange}
                  />
                </div>
                <div className="col-md-12">
                  {/* Submit button */}
                  {editingTreatment ? (
                    <button
                      type="button"
                      className="btn btn-primary mt-2"
                      onClick={handleFormSubmit}
                    >
                      Update Treatment
                    </button>
                  ) : (
                    <button
                      type="button"
                      className="btn btn-primary mt-2"
                      onClick={handleFormSubmit}
                    >
                      Add Treatment
                    </button>
                  )}
                </div>
              </div>
            </form>
          </div>
          {/* Display treatments */}
          <div className="col-md-12 mt-4">
            <h3>Treatments</h3>
            <div className="row">
              {treatments.map(treatment => (
                <div className="col-md-4" key={treatment._id}>
                  <div className="card mb-3">
                    <img
                      src={treatment.image}
                      className="card-img-top"
                      alt={treatment.title}
                    />
                    <div className="card-body">
                      <h5 className="card-title">{treatment.title}</h5>
                      <p className="card-text">{treatment.description}</p>
                      <p className="card-text">{treatment.briefDescription}</p>
                      <button
                        className="btn btn-outline-danger"
                        onClick={() => handleDelete(treatment._id)}
                      >
                        Delete
                      </button>
                      <button
                        className="btn btn-outline-primary"
                        onClick={() => handleEdit(treatment)} 
                      >
                        Edit
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
